import React, { useEffect, useState, useLayoutEffect } from 'react';
import 'react-native-gesture-handler';
import { ImageBackground } from 'react-native';
import styles from './styles';
import { Switch } from "react-native";
import Loading from '../../components/Loading';
import DegreeDetails from '../../components/DegreeDetails';
import { getRandomDegree } from '../../utils';

export default function SearchScreen({ navigation }) {

	const [isLoaded, setIsLoaded] = useState(false);
	const [randomDegree, setRandomDegree] = useState([]);
	const [toggleSwitch, setToggleSwitch] = useState(false);

	useEffect(() => {
		getRandomDegree()
			.then((random) => {
				setRandomDegree(random);
				setIsLoaded(true);
		})
	}, [])

	useLayoutEffect(() => {
		navigation.setOptions({
			headerRight: () => (
				<Switch
					value={toggleSwitch}
					onValueChange={(value) => setToggleSwitch(value)} />)
		})
	}, [toggleSwitch]);

	return (
		<>
			{isLoaded ? randomDegree.map(data =>
				<DegreeDetails
					sign={data.sign}
					degree={data.degree}
					title={data.title}
					keynote={data.keynote}
					description={data.description}
					toggleSwitch={toggleSwitch} />
				) : (
					<ImageBackground source={require('../../static/images/blackHole.jpeg')} style={styles.loadingPageImage}>
						<Loading />
					</ImageBackground>
				)}
		</>
	)
}
